  cargarAnios() {
    const fechaActual = new Date();
    const anioActual = +format(fechaActual, 'Y');

    for (let i = anioActual - 10; i <= anioActual; i++) {
      this.anios.unshift({
        value: i,
        label: i.toString()
      });
    }
  }

  cargarMeses(anio) {
    const fechaActual = new Date();
    const anioActual = +format(fechaActual, 'Y');
    const mesActual = +format(fechaActual, 'M');
    const diaActual = +format(fechaActual, 'd');
    const diaLimite = 20; // 31

    let mesMaximo = 0;

    if (anio === anioActual) {
      if (diaActual >= diaLimite && mesActual > 1) {
        mesMaximo = mesActual - 1;
      } else {
        mesMaximo = mesActual - 2;
      }

      this.meses = meses.filter(mes => +mes.value <= mesMaximo);
    } else {
      if (anio + 1 == anioActual) {
        mesMaximo = 12;

        if (mesActual == 1 && diaActual < diaLimite) {
          mesMaximo = mesMaximo - 1;
        }

        this.meses = meses.filter(mes => +mes.value <= mesMaximo);
      } else {
        this.meses = meses;
      }
    }

    this.mes = 0;
  }

  actualizarAcepto() {
    this.sharedService.cambiarEstado(true);
    this.docService.actualizarAcepto(this.numEmpleado).pipe(finalize(() => this.sharedService.cambiarEstado(false))).subscribe({
      next: data => {
        this.messageService.add({
          severity: 'success',
          summary: TITLES.success,
          detail: 'El registro ha sido evaluado.'
        });
        this.aceptado = true;
      },
      error: err => {
        this.messageService.add({
          severity: 'error',
          summary: TITLES.error,
          detail: err.error
        });
      }
    });
  }

}

